import { useEffect, useState } from "react"
import { useDispatch, useSelector } from "react-redux"
import { chatRequest } from "features/chatbot/reducer/chatSlice"

export function Ask(props){
    // const answer = useSelector(state => state.chat.chatData)
    const dispatch = useDispatch()
    const [ text, setText ] = useState('')
    // const [ count, setCount] = useState(0)
    // const [ chatanswer, setChatAnswer] = useState({
    //   data: {
    //     chatAnswer : "",
    //     chatKey: ""
    //   }
    // })
   
    const key = props.previousStep.key
    const botAnswer = {
      [key]:useSelector((state)=>state.chatData[key])
    }
    useEffect(()=>{
      setText(props.steps.userinput.value)
      // console.log(JSON.stringify(state.chatData))
      dispatch(chatRequest({'chatkey': props.previousStep.key,'chatAnswer':props.steps.userinput.value}))
    },[text])
    
    // if(answer != null && count < 1){
    //     setCount(count+1),
    //     setChatAnswer(answer)
    // }
      return (
        <>
        {/* <div>{chatanswer.data.chatAnswer}</div> */}
        <div>{botAnswer[key]}</div>
        </>
      );
  }
  
  
export function ResetMessage(){
return(<>
    <div>
    또 다른 궁금하신 부분이 있나요?
    </div>
</>)
}
