import ChatBot from "react-simple-chatbot";
import React from "react";
import { ThemeProvider } from "styled-components";
import { useDispatch } from "react-redux";
import { initStatus } from "../reducer/chatbotSlice";
import { AlertMessage, Ask, ResetMessage } from "./ChatModules";

export default function Chat() {
  const dispatch = useDispatch()
  dispatch(initStatus())
  return (
    <ThemeProvider
      theme={{
        background: "#f5f8fb",
        fontFamily: "Helvetica Neue",
        headerBgColor: "#EF6C00",
        headerFontColor: "#fff",
        headerFontSize: "15px",
        botBubbleColor: "#EF6C00",
        botFontColor: "#fff",
        userBubbleColor: "#fff",
        userFontColor: "#4a4a4a",
      }}
    >
      <ChatBot
        steps={[
          {
            id: 'welcome',
            message: '안녕하세요 JARVIS 에요 궁금한 사항이 있나요?',
            trigger: 'question',
          },
          {
            id: 'question',
            message: '질문하실 말이 있나요?',
            trigger: 'userinput'
          },
          {
            id: 'userinput',
            user: true,
            trigger: 'answer'
          },
          {
            id: "askBot",
            component: <Ask />,
            asMessage: true,
            trigger: 'userinput'
          },
        ]}
        botAvatar={require("./data/icon.png").default}
        userAvatar={require("./data/user.png").default}
      />
    </ThemeProvider>
  );
};
